const { Struct } = require('struct');
const net = require('net');
const { formatServerData } = require('./toStruct_Data');
const { createCmdStruct } = require('./struct_cmdData');

// 88888888 04CC0120 2000 000000000000 
// 88888888 04cc0120 2000 000000000000 
const cmdStruct = createCmdStruct();

function updateStructCommands(data) {
  const {
    MdlManChr, MdlManInj, MdlManCdr, MdlManCar,
  } = data;

  cmdStruct.fields.CmdMemVal[0].MdlManChr = MdlManChr;
  cmdStruct.fields.CmdMemVal[0].MdlManInj = MdlManInj;
  cmdStruct.fields.CmdMemVal[0].MdlManCdr = MdlManCdr;
  cmdStruct.fields.CmdMemVal[0].MdlManCar = MdlManCar;
  cmdStruct.fields.CmdMemVal[0].MdlExhAcv = false;
  cmdStruct.fields.CmdMemVal[0].MdlMisAcv = true;
  cmdStruct.fields.CmdMemVal[0].MdlIgnAcv = 0x55;
  cmdStruct.fields.CmdMemVal[0].MdlAlmAcv = false;
}

function sendData() {
  const buf = cmdStruct.buffer();
  return buf;
}

module.exports = { updateStructCommands, sendData };
